mapper.select:
select * from USERDATA
where usercode = #{usercode}
  and phone = #{phone}

mapper.update:
update USERDATA
set agreeyn = #{agreeyn}
where userseq = #{userseq}

update USERDATA
set phone = #{phone}
,agreeyn = #{agreeyn}
,startdate = #{startdate} 
,enddate = #{enddate}
where userseq = #{userseq}
